/*

powmod takes in bigint #s and returns a^b % n
a^b = a * a * a...
*/

function powmod(a, b, n) {
  //base case:
  if (b === 0) {
    return 1;
  }
  if (b === 1) {
    return a;
  }
  //recursive case:
  return (powmod(a, b / 2 + 1, n) * powmod(a, b / 2, n)) % n;
}

