function addmod(a, b, n) {
  //base case: b = 0 / 1
  if (b == 0n) {
    return 0n;
  }
  if (b == 1n) {
    return a;
  }
  return (addmod(a, b / 2n + 1n, n) + addmod(a, b / 2n, n)) % n;
}

